import React from 'react';
import './FilterSection.css';
import { Dropdown } from 'flowbite';


function initializeDropdown() {
  const $targetEl = document.getElementById('dropdownMenu');
  const $triggerEl = document.getElementById('dropdownButton');

  const options = {
    placement: 'bottom',
    triggerType: 'click',
    offsetSkidding: 0,
    offsetDistance: 10,
    delay: 300,
  };

  const locationDropdown = new Dropdown($targetEl, $triggerEl, options);
  return locationDropdown;
}

function FilterSection(props) {
  const locationDropdown = initializeDropdown()
  return (
    <div className='px-12 py-5 mx-24 mt-5'>
      <div className='flex justify-between'>
        <h1 className='text-4xl font-semibold'>Search properties to rent</h1>
        <input onKeyUp={props.handleSearchChange} type="search" id="default-search" class="block p-4 h-10 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 focus:ring-violet-400 focus:border-violet-500" placeholder="Search" required />
      </div>
      <div>
        <button id="dropdownButton" onClick={loca} class="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2.5 text-center inline-flex items-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800" type="button">Dropdown button <svg aria-hidden="true" class="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M19 9l-7 7-7-7"></path></svg></button>
        <div id="dropdownMenu" class="z-10 hidden bg-white divide-y divide-gray-100 rounded-lg shadow w-44 dark:bg-gray-700">
          <ul class="py-2 text-sm text-gray-700 dark:text-gray-200" aria-labelledby="dropdownButton">
            <li>
              <a href="#" class="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">Dashboard</a>
            </li>
            <li>
              <a href="#" class="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">Settings</a>
            </li>
            <li>
              <a href="#" class="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">Earnings</a>
            </li>
            <li>
              <a href="#" class="block px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">Sign out</a>
            </li>
          </ul>
        </div>
      </div>
    </div>
  )
}

export default FilterSection
